import './App.css';
// import Home from './components/Home';
// import About from './components/About';
// import Education from './components/Education';
// import Contact from './components/Contact';
// import Skills from './components/Skills';
// import Projects from './components/Projects';
import React, { useState, useEffect } from "react";
import lottie from "lottie-web";
import location from "./loading5.json";

function App() {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true); // Initialize loading state to true
  const [completed, setCompleted] = useState(false);

  React.useEffect(() => {
    lottie.loadAnimation({
      container: document.querySelector("#load"),
      renderer: "svg",
      autoplay: true,
      animationData: location
    });
  }, []);

    useEffect(() => {
      const timeoutId = setTimeout(() => {
      setLoading(false); // Set loading to false after 2 seconds
      // setMessage('Delayed message after 2 seconds!');
    }, 300000);

    // Cleanup function to clear the timeout if the component unmounts
    return () => clearTimeout(timeoutId);
  }, []);

  return (
    <>
      {loading ? ( // Render loading animation if loading is true
        <div style={{ width: "100%", height: "100vh", display: "flex", justifyContent: "center", alignItems: "center", backgroundColor: "black" }}>
          <div id='load' style={{ width: "40%", height: "70%" }}/>
        </div>
      ) : (
          <h1>Loaded</h1>
        // Render components once loading is false
        // <>
        //   <Home />
        //   <About />
        //   <Education />
        //   <Skills />
        //   <Projects />
        //   <Contact />
        // </>
      )}
    </>
  );
}

export default App;
