import './App.css';
import Home from './components/Home';
import About from './components/About';
import Education from './components/Education';
import Contact from './components/Contact';
import Skills from './components/Skills';
import Projects from './components/Projects';
import {React, useState,useEffect } from "react";
// import { HashLoader } from 'react-spinners';
import lottie from "lottie-web";
import location from "./loading.json";

function App() {
  React.useEffect(() => {
    lottie.loadAnimation({
      container: document.querySelector("#react-logo"),
      renderer: "svg",
      loop: true,
      autoplay: true,
      animationData: location
    });
  }, []);
  const [show, setShow] = useState(false);
  useEffect(() => {
    setShow(true)
    setTimeout(() => {
      setShow(false)
    }, 3500)
  },[])
  return (
    <>
        {
        show ?
           <div style={{ width: "100%", height: "100vh", display: "flex", justifyContent: "center", alignItems: "center", backgroundColor: "black" }}>
          <div id='react-logo' style={{ width: 200, height: 200 }}>
            {/* <HashLoader color="#00ffcc" size={100} /> */}
          </div>
        </div>  
            :
            <>
            <Home></Home>
            <About></About>
            <Education></Education>
            <Skills></Skills>
            <Projects></Projects>
            <Contact></Contact>
            </>
        }

    </>
  );
}

export default App;
